<section class:clip={toggle_settings} class="settings ">
    <div class="set">
        <div class="header">
            <div class="close">
                <button on:click={()=>{toggle_settings = !toggle_settings}}>
                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M4.29825 5.70175L18.2982 19.7018C18.4737 19.8947 18.7193 20 19 20C19.5439 20 20 19.5439 20 19C20 18.7193 19.8947 18.4737 19.7018 18.2982L5.70175 4.29824C5.52632 4.10526 5.2807 4 5 4C4.45614 4 4 4.45614 4 5C4 5.2807 4.10526 5.52632 4.29825 5.70175ZM18.2982 4.29824L4.29825 18.2982C4.10526 18.4737 4 18.7193 4 19C4 19.5439 4.45614 20 5 20C5.2807 20 5.52632 19.8947 5.70175 19.7018L19.7018 5.70175C19.8947 5.52632 20 5.2807 20 5C20 4.45614 19.5439 4 19 4C18.7193 4 18.4737 4.10526 18.2982 4.29824Z" fill="#333333"/>
                        </svg>
                </button>
            </div>
            <div class="title">
                <h2>settings</h2>
            </div>

        </div>
        <hr>
        <main>
            <ul>
                <li><button on:click={()=>{toggle_theme = !toggle_theme}}>
                    dark theme 
                </button></li>
                <li><button on:click={()=>{toggle_pictures = !toggle_pictures}}>
                    dont show pictures
                </button></li>
                <li><button on:click={()=>{toggle_progress = !toggle_progress}}>
                    dont show progress bar
                </button></li>
            </ul>
        </main>
        <footer></footer>
    </div>
</section>

<style lang="scss">
    :global(body){
        background: var(--background);
    }
    .clip{
        pointer-events: all !important;
        opacity: 100% !important;
        clip-path: circle(1500px) !important;
        transition: all 1.2s ease-in-out;
    }
    header{
        @include fixed(8);
        top: 0;
        nav{
            background: var(--background);
            @include flex;
            h1{
                padding: 10px;
                a{
                    color: var(--text);
                    @include text(var(--text), var(--rem0), 450);
                }
            }
            .settings_button{
                button{
                    padding: 10px;
                }
            }
        }
    }
    footer{
        @include fixed(8);
        bottom: 0;
        nav{
            background: var(--white);
            margin: 15px 5px ;
            border: black solid 1px;
            ul{
                @include flex;
                padding: 3px;
                li{
                    a{
                        display: block;
                        padding: 5px;
                    }
                }
            }
        }

    }
    .settings{
        transition: all 1.2s ease-in-out;
        @include fixed(9);
        height: 100vh;
        background: var(--white);
        top: 0;
        opacity: 0;
        pointer-events: none;
        clip-path: circle(0);
        .set{
            background-color: var(--white);
            margin: 15px;
            .header{
                @include flex;
                padding: 10px 0;
                .title{
                    h2{
                        @include text(var(--text), 0.9rem, 500);
                    }
                }
            }
            main{
                ul{
                    li{
                        padding: 10px;
                        button{
                            width: 100%;
                            border: var(--blue) solid 1px;
                            padding: 10px;
                            border-radius: 5px;
                            @include text(var(--text), 1rem, 500);

                        }
                    }
                }
            }

        }

    }
</style>